CREATE DATABASE `meridional-v1.0` DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci;
USE `meridional-v1.0`;


CREATE TABLE IF NOT EXISTS `usuarios` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `login` varchar(200) NOT NULL,
  `senha` varchar(20) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;

CREATE TABLE IF NOT EXISTS `propostas` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `renovacao` date NOT NULL,
  `proposta` int(11) DEFAULT NULL,
  `segurado` varchar(200) DEFAULT NULL,
  `cia` varchar(100) NOT NULL,
  `tipo` varchar(50) NOT NULL,
  `detalhes` varchar(255) DEFAULT NULL,
  `apolice` varchar(50) DEFAULT NULL,
  `vencimento` date DEFAULT NULL,
  `prem_liq` decimal(10,2) DEFAULT NULL,
  `comissao` int(11) DEFAULT NULL,
  `status` varchar(20) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8;

CREATE TABLE IF NOT EXISTS `table 5` (
  `COL 1` varchar(10) DEFAULT NULL,
  `COL 2` varchar(8) DEFAULT NULL,
  `COL 3` varchar(33) DEFAULT NULL,
  `COL 4` varchar(10) DEFAULT NULL,
  `COL 5` varchar(45) DEFAULT NULL,
  `COL 6` varchar(10) DEFAULT NULL,
  `COL 7` varchar(10) DEFAULT NULL,
  `COL 8` varchar(12) DEFAULT NULL,
  `COL 9` varchar(8) DEFAULT NULL,
  `COL 10` varchar(63) DEFAULT NULL
) ENGINE=MyISAM DEFAULT CHARSET=utf8;


